import { useState, useCallback } from "react";
import ReactFlow, {
  Controls,
  Background,
  applyNodeChanges,
  applyEdgeChanges,
  MiniMap
} from "reactflow";
import "reactflow/dist/style.css";

import {initialNodes} from '../node.components.jsx'
import {initialEdges}from '../edges.component'

import highLevelNode from '../custom-nodes/highlevel-node/highlevel-node.component'
import specificLevelNode from '../custom-nodes/specific-node/specific-node.component'
import consequenceNodeComponent from "../custom-nodes/consequence-node/consequence-node.component.jsx";

import { StyledFlow, MiniMapStyled } from "./flow.styles";

const nodeTypes = {
  highLevelNode: highLevelNode,
  specificLevelNode: specificLevelNode,
  consequenceNode: consequenceNodeComponent,


}

const Flow=(props) =>{
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState(initialEdges);

  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => {
        return(applyNodeChanges(changes, nds))}),
    []
  );
  const onEdgesChange = useCallback(
    (changes) => setEdges((eds) => applyEdgeChanges(changes, eds)),
    []
  );

  const NODE_THEMES={
    highLevelNode:"highLevelNodeBG",
    specificLevelNode:"specificLevelNodeBG",
    consequenceNode:"consequenceNodeBG"
  }

  const nodeColor=(node)=>{
    console.log(props);
  }

  return (
    <div style={{ height: "100%" }}>
      <StyledFlow
        nodes={nodes}
        onNodesChange={onNodesChange}
        edges={edges}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
      >
        <Background />
        <Controls />
        <MiniMapStyled zoomable pannable nodeColor={nodeColor}/>
      </StyledFlow>
    </div>
  );
}

export default Flow;
