import styled from "styled-components";

export const StyledNode = styled.div`
  padding: 10px 20px;
  border-radius: 5px;
  font-size: ${({theme,node}) => theme[node.type].fontSize};
  background-color: ${({theme,node, selected}) => theme[node.type].bgC};
  color: #fff;
  border: 2px solid ${(props) => (props.selected ? "#ff0072" : "#41436a")};
`;
