import styled from "styled-components";

export const StyledNode = styled.div`
  padding: 10px 20px;
  border-radius: 5px;
  font-size: ${({ theme, node, selected }) => selected?'20px' :theme[node.type].fontSize};
  background-color: ${({ theme, node, selected }) =>
    (node.data.anySelected && !selected)
      ? theme.passiveNodeBgC
      : theme[node.type].bgC};
  color: #fff;
  border: 2px solid ${(props) => (props.selected ? "#ff0072" : "#41436a")};
`;
